void CrearMapa() {
	for (int fila = 0; fila < 40; fila++) {
		for (int columna = 0; columna < 130; columna++) {
			Console::SetCursorPosition(columna, fila);
			if (Mapa[fila][columna] == 0) Console::ForegroundColor = ConsoleColor::Black;
			if (Mapa[fila][columna] == 1) Console::ForegroundColor = ConsoleColor::White;
			if (Mapa[fila][columna] == 2) Console::ForegroundColor = ConsoleColor::DarkYellow;
			cout << (char)219;
		}
	}
}

